INSERT INTO `dc_articles` (`id`, `store_id`, `cat_id`, `slug`, `title`, `keyword`, `sapo`, `detail`, `viewed`, `date_created`, `date_update`, `status`, `properties`, `position`, `home`) VALUES
(1, 1, 4, 'nam-2015-bat-dong-san-se-khoi-sac-hon', 'Năm 2015, bất động sản sẽ khởi sắc hơn', 'Năm 2015, bất động sản sẽ khởi sắc hơn', 'Theo kết quả khảo sát của CBRE Việt Nam, trong quý 3/2014 hoạt động chào bán trong phân khúc thị trường căn hộ bán tại TP.HCM vẫn tấp nập.', '<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	<strong style="margin: 0px; padding: 0px;">Theo kết quả khảo s&aacute;t của CBRE Việt Nam, trong qu&yacute; 3/2014 hoạt động ch&agrave;o b&aacute;n trong ph&acirc;n kh&uacute;c thị trường căn hộ b&aacute;n tại TP.HCM vẫn tấp nập. Đ&atilde; c&oacute; 3.104 căn hộ được ch&agrave;o b&aacute;n trong qu&yacute; 3 với hầu hết c&aacute;c dự &aacute;n ch&agrave;o b&aacute;n thuộc ph&acirc;n kh&uacute;c cao cấp v&agrave; b&igrave;nh d&acirc;n.</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/tuan/102014/23/04/041832baoxaydung_image001.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 375px;" /><br style="margin: 0px; padding: 0px;" />\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Dự b&aacute;o năm 2015 thị trường bất động sản sẽ khởi sắc hơn</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Khảo s&aacute;t cũng cho thấy, trong 9 th&aacute;ng năm 2014, số căn hộ ch&agrave;o b&aacute;n tại TP.HCM đ&atilde; đạt 8.393 căn, cao hơn số lượng c&aacute;c căn hộ được ch&agrave;o b&aacute;n trong cả năm 2013 v&agrave; cao gấp 3 lần số lượng căn hộ ch&agrave;o b&aacute;n trong năm 2012. B&ecirc;n cạnh đ&oacute;, doanh số b&aacute;n trong 9 th&aacute;ng đạt kh&aacute; ấn tượng với khoảng 50% số căn hộ ch&agrave;o b&aacute;n đ&atilde; được ti&ecirc;u thụ. Giao dịch v&agrave; gi&aacute; b&aacute;n của thị trường căn hộ tăng ở tất cả c&aacute;c ph&acirc;n kh&uacute;c.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	B&agrave; Dương Th&ugrave;y Dung- Ph&oacute; gi&aacute;m đốc ki&ecirc;m trưởng ph&ograve;ng Nghi&ecirc;n cứu &amp; tư vấn CBRE- cho rằng, c&oacute; được kết quả t&iacute;ch cực như tr&ecirc;n phải kể đến sự nỗ lực của c&aacute;c chủ đầu tư trong việc chủ động điều chỉnh căn hộ ph&ugrave; hợp với khả năng của người mua. Ngo&agrave;i việc điều chỉnh diện t&iacute;ch căn hộ giảm xuống &iacute;t nhất 20% so với trước đ&acirc;y, cơ cấu căn hộ cũng được điều chỉnh giảm xuống với tỉ lệ căn hộ c&oacute; từ 1 đến 2 ph&ograve;ng ngủ chiếm tỉ trọng cao hơn (trước đ&acirc;y chủ yếu l&agrave; c&aacute;c căn hộ từ 3 đến 4 ph&ograve;ng ngủ). Số lượng chủ đầu tư tham gia ch&agrave;o b&aacute;n c&aacute;c dự &aacute;n căn hộ b&igrave;nh d&acirc;n cũng đ&atilde; tăng l&ecirc;n 48% trong năm 2014 so với 31% ở thời điểm năm 2010.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Ph&acirc;n kh&uacute;c mặt bằng văn ph&ograve;ng v&agrave; mặt bằng b&aacute;n lẻ cũng đang c&oacute; sự điều chỉnh tăng gi&aacute; nhẹ ở khu vực trung t&acirc;m do nguồn cung hạn chế. Hiện tỉ lệ mặt bằng trống ở hai ph&acirc;n kh&uacute;c n&agrave;y chỉ c&ograve;n khoảng 10%.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Nhận định về mặt bằng l&atilde;i suất cho thị trường bất động sản hiện nay, b&agrave; Dương Th&ugrave;y Dung cho biết, hiện c&oacute; nhiều ng&acirc;n h&agrave;ng cho vay với mức l&atilde;i suất từ 7,5 đến 8,5%/năm, nhưng đ&acirc;y chỉ l&agrave; mức l&atilde;i suất d&agrave;nh cho c&aacute;c khoản vay trong 6 th&aacute;ng đầu ti&ecirc;n, sau đ&oacute; l&atilde;i suất sẽ được điều chỉnh l&ecirc;n khoảng từ 10- 12%/năm. Tuy nhi&ecirc;n, mức l&atilde;i suất n&agrave;y cũng đ&atilde; giảm nhiều so với 2, 3 năm trước đ&acirc;y. B&ecirc;n cạnh yếu tố l&atilde;i suất, sự ph&aacute;t triển cơ sở hạ tầng trọng điểm như tuyến Metro số 1 v&agrave; sự hấp dẫn về tỉ suất lợi nhuận của BĐS so với c&aacute;c k&ecirc;nh đầu tư kh&aacute;c cũng l&agrave; những yếu tố t&iacute;ch cực cho thị trường BĐS TP.HCM trong thời gian sắp tới.</p>\r\n<p class="author_article right" style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; float: right; color: rgb(75, 75, 75); font-weight: bold; text-align: justify;">\r\n	Theo B&aacute;o C&ocirc;ng thương</p>', 0, '2014-10-23 10:29:04', '', 1, 'a:5:{s:6:"avatar";s:39:"316857381_041832baoxaydung_image001.jpg";s:6:"photos";a:0:{}s:6:"videos";a:0:{}s:5:"files";a:0:{}s:11:"user_upload";s:8:"derasoft";}', 1, 0),
(2, 1, 4, 'tp-hcm-kiem-ke-dat-dai-va-lap-ban-do-hien-trang-su-dung-dat-nam-2014', 'TP.HCM kiểm kê đất đai và lập bản đồ hiện trạng sử dụng đất năm 2014', 'TP.HCM kiểm kê đất đai và lập bản đồ hiện trạng sử dụng đất năm 2014', '(Xây dựng) - TP.HCM vừa giao Sở Tài nguyên & Môi trường chủ trì phối hợp với các Sở, ngành, UBND các quận, huyện lập phương án kiểm kê đất đai, lập bản đồ hiện trạng sử dụng đất năm 2014, trình UBND thành phố phê duyệt.', '<h3 style="font-family: Arial, Helvetica, sans-serif; margin: 5px 0px 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; font-size: 12px; line-height: 17px; font-weight: normal;">\r\n	<strong style="margin: 0px; padding: 0px;">(X&acirc;y dựng) -&nbsp;<span style="font-family: Arial, Verdana, sans-serif; margin: 0px; padding: 0px; color: rgb(34, 34, 34);">TP.HCM vừa giao Sở T&agrave;i nguy&ecirc;n &amp; M&ocirc;i trường chủ tr&igrave; phối hợp với c&aacute;c Sở, ng&agrave;nh, UBND c&aacute;c quận, huyện lập phương &aacute;n kiểm k&ecirc; đất đai, lập bản đồ hiện trạng sử dụng đất năm 2014, tr&igrave;nh UBND th&agrave;nh phố ph&ecirc; duyệt.</span></strong></h3>\r\n<h3 style="font-family: Arial, Helvetica, sans-serif; margin: 5px 0px 0px; padding: 0px; color: rgb(0, 0, 0); font-size: 12px; line-height: 17px; font-weight: normal; text-align: center;">\r\n	<strong style="margin: 0px; padding: 0px;"><span style="font-family: Arial, Verdana, sans-serif; margin: 0px; padding: 0px; color: rgb(34, 34, 34);"><img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/23/00/002744baoxaydung_3.jpg" style="font-family: Arial, Helvetica, sans-serif; margin: 0px; padding: 0px; width: 500px; height: 337px;" /></span></strong><br style="margin: 0px; padding: 0px;" />\r\n	<br style="margin: 0px; padding: 0px;" />\r\n	<span style="font-family: Arial, Verdana, sans-serif; margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Một dự &aacute;n tr&ecirc;n địa b&agrave;n TP.HCM</span></h3>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	<span style="margin: 0px; padding: 0px; line-height: 17px;">Đồng thời, chủ tr&igrave;, phối hợp với Sở Nội vụ v&agrave; c&aacute;c Sở, ng&agrave;nh li&ecirc;n quan tham mưu tr&igrave;nh UBND th&agrave;nh phố th&agrave;nh lập Ban chỉ đạo kiểm k&ecirc; đất đai năm 2014.</span></p>', 0, '2014-10-23 10:30:42', '', 1, 'a:5:{s:6:"avatar";s:33:"1206819132_002744baoxaydung_3.jpg";s:6:"photos";a:0:{}s:6:"videos";a:0:{}s:5:"files";a:0:{}s:11:"user_upload";s:8:"derasoft";}', 2, 0),
(3, 1, 4, 'giai-quyet-no-xau-de-phuc-hoi-thi-truong', 'Giải quyết nợ xấu để phục hồi thị trường', 'Giải quyết nợ xấu để phục hồi thị trường', '(Xây dựng) - “Đón đầu cơ hội hồi phục thị trường bất động sản” là chủ đề buổi Hội thảo do Tạp chí Đầu tư bất động sản CafeLand tổ chức ngày 21/10 tại TP.HCM.', '<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	<strong style="margin: 0px; padding: 0px;">(X&acirc;y dựng) - &ldquo;Đ&oacute;n đầu cơ hội hồi phục thị trường bất động sản&rdquo; l&agrave; chủ đề buổi Hội thảo do Tạp ch&iacute; Đầu tư bất động sản CafeLand tổ chức ng&agrave;y 21/10 tại TP.HCM.</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/23/00/000538baoxaydung_16.jpg" style="margin: 0px; padding: 0px; width: 496px; height: 332px;" /><br style="margin: 0px; padding: 0px;" />\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Nhiều &yacute; kiến tại hội thảo cho biết, hiện c&aacute;c chủ đầu tư đ&atilde; c&oacute; niềm tin hơn để tung ra thị trường c&aacute;c sản phẩm BĐS.</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Tại Hội thảo, Thạc sĩ Hồ B&aacute; T&igrave;nh nhận định: &ldquo;Thị trường BĐS Việt Nam c&oacute; điểm chung l&agrave; tăng trưởng n&oacute;ng, tạo cơn sốt để rồi sau đ&oacute; suy giảm dần v&agrave; đ&oacute;ng băng. Cụ thể, thị trường tạo cơn sốt năm 1993 - 1994, nhưng đ&oacute;ng băng từ năm 1995 - 1999; b&ugrave;ng nổ năm 2001 - 2002 v&agrave; nguội lạnh từ cuối năm 2002 đến năm 2006. Từ năm 2007 - 2008, thị trường nh&agrave; đất sốt lại nhưng nhanh ch&oacute;ng suy giảm sau đ&oacute;, đến nay mới c&oacute; dấu hiệu hồi phục&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Ở g&oacute;c độ QLNN, &ocirc;ng Trần Trọng Tuấn - Gi&aacute;m đốc Sở X&acirc;y dựng TP.HCM cho rằng: Để thị trường BĐS phục hồi cần thiết phải c&oacute; sự nỗ lực của nhiều ph&iacute;a, trong đ&oacute; c&oacute; nh&agrave; đầu tư, kh&aacute;ch h&agrave;ng v&agrave; cơ quan quản l&yacute;. Cần c&oacute; c&aacute;c giải ph&aacute;p l&acirc;u d&agrave;i cũng như trước mắt từ c&ocirc;ng cụ t&agrave;i ch&iacute;nh, cơ chế của Nh&agrave; nước v&agrave; nh&agrave; đầu tư. Đối với nh&agrave; đầu tư, khi thị trường BĐS đ&atilde; c&oacute; dấu hiệu phục hồi th&igrave; phải nắm bắt cơ hội, đưa ra chiến lược ph&ugrave; hợp dựa tr&ecirc;n ch&iacute;nh s&aacute;ch chăm s&oacute;c kh&aacute;ch h&agrave;ng v&agrave; c&aacute;c quy định của Nh&agrave; nước. Về t&igrave;nh h&igrave;nh c&aacute;c dự &aacute;n tại TP.HCM, &ocirc;ng Tuấn th&ocirc;ng tin: Hiện th&agrave;nh phố c&oacute; hơn 1.400 dự &aacute;n ph&aacute;t triển nh&agrave; ở, trong đ&oacute; dự &aacute;n ho&agrave;n th&agrave;nh chỉ chiếm 3,36%, gần 49% dự &aacute;n đang gặp kh&oacute; khăn. Từ đầu năm 2014 đến nay c&oacute; khoảng 5.700 căn hộ đ&atilde; được b&aacute;n, tăng 83% so với c&ugrave;ng kỳ năm 2013, chủ yếu l&agrave; căn hộ dưới 70 m2.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/23/00/000538baoxaydung_17.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 338px;" /><br style="margin: 0px; padding: 0px;" />\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Đa số &yacute; kiến tại Hội thảo đều cho rằng, để thị trường BĐS phục hồi v&agrave; ph&aacute;t triển cần giải quyết dứt điểm nợ xấu.</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Nhiều &yacute; kiến nhận định tại Hội thảo cho biết, hiện c&aacute;c chủ đầu tư đ&atilde; c&oacute; niềm tin hơn để tung ra thị trường c&aacute;c sản phẩm BĐS v&igrave; sức ti&ecirc;u thụ đ&atilde; đạt mức 58%, đặc biệt l&agrave; ph&acirc;n kh&uacute;c b&igrave;nh d&acirc;n đạt 60%. Trong 9 th&aacute;ng năm 2014, tại TP.HCM đ&atilde; c&oacute; gần 8.400 căn ch&agrave;o b&aacute;n, vượt cả năm 2013 v&agrave; 2012, trong số đ&oacute; gần 50% đ&atilde; được ti&ecirc;u thụ. Một số dự &aacute;n chậm triển khai, bị &ldquo;ng&acirc;m&rdquo; trong thời gian d&agrave;i th&igrave; nay đang được khẩn trương triển khai như dự &aacute;n Metropolis Thảo Điền (quận 2, gần 3.000 căn), Vinhomes Central Park (quận B&igrave;nh Thạnh, 10.000 căn).</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Theo b&agrave; Dương Th&ugrave;y Dung, Ph&oacute; Gi&aacute;m đốc ki&ecirc;m Trưởng ph&ograve;ng Nghi&ecirc;n cứu v&agrave; Tư vấn, C&ocirc;ng ty CBRE Việt Nam: &ldquo;Hoạt động mở b&aacute;n vẫn c&ograve;n nhộn nhịp, nhiều khuyến m&atilde;i hơn, ch&iacute;nh s&aacute;ch trả chậm được &aacute;p dụng phổ biến; l&ograve;ng tin người ti&ecirc;u d&ugrave;ng về thị trường đang dần hồi phục; l&atilde;i suất cho vay của ng&acirc;n h&agrave;ng giảm dần; một số c&ocirc;ng tr&igrave;nh giao th&ocirc;ng trọng điểm đang triển khai khiến thị trường BĐS trở n&ecirc;n hấp dẫn hơn&rdquo;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Tiến sĩ Vũ Đ&igrave;nh &Aacute;nh cho rằng: mặc d&ugrave; thị trường BĐS đang hồi phục nhưng vẫn c&ograve;n nhiều vấn đề cần phải giải quyết. Nhiều ng&acirc;n h&agrave;ng hiện nay đang l&acirc;m t&igrave;nh cảnh &ldquo;sống dở chết dở&rdquo; khi nợ xấu của nhiều dự &aacute;n bất động sản kh&ocirc;ng thể xử l&yacute; nổi, to&agrave;n người b&aacute;n nhưng b&aacute;n kh&ocirc;ng ai mua. Gi&aacute; bất động sản cũng kh&oacute; c&oacute; hy vọng xuống hơn được nữa v&igrave; c&oacute; nhiều chi ph&iacute; kh&ocirc;ng t&iacute;nh được, kh&ocirc;ng thể c&ocirc;ng khai được v&agrave; người ti&ecirc;u d&ugrave;ng vẫn phải g&aacute;nh chịu.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Đồng quan điểm, một số chuy&ecirc;n gia khuyến nghị, để tiếp tục kh&ocirc;i phục thị trường bất động sản cần phải giải quyết vấn đề nợ xấu bằng c&aacute;ch tăng quyền lực cho C&ocirc;ng ty Quản l&yacute; t&agrave;i sản (VAMC), tăng tiền tươi để mua dứt điểm nợ xấu; cải c&aacute;ch mạnh mẽ thủ tục h&agrave;nh ch&iacute;nh, nhất l&agrave; thủ tục đất đai, đồng thời tạo điều kiện cho ph&eacute;p nh&agrave; đầu tư nước ngo&agrave;i đầu tư v&agrave;o nh&agrave; ở tại Việt Nam.</p>', 0, '2014-10-23 10:31:39', '', 1, 'a:5:{s:6:"avatar";s:34:"1445156314_000538baoxaydung_16.jpg";s:6:"photos";a:0:{}s:6:"videos";a:0:{}s:5:"files";a:0:{}s:11:"user_upload";s:8:"derasoft";}', 2, 0),
(4, 1, 4, 'bi-quyet-nao-giup-cac-nha-dau-tu-vuon-len-trong-khung-hoang', 'Bí quyết nào giúp các nhà đầu tư vươn lên trong khủng hoảng', 'Bí quyết nào giúp các nhà đầu tư vươn lên trong khủng hoảng', '(Xây dựng) - Trong bối cảnh thị trường bất động sản tụt dốc, hàng loạt công ty, dự án bất động sản rơi vào vũng lầy thì vẫn có không ít những chủ đầu tư biết vươn lên, chớp lấy cơ hội để khẳng định vị thế trên thị trường.', '<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	<strong style="margin: 0px; padding: 0px;">(X&acirc;y dựng) - Trong bối cảnh thị trường bất động sản tụt dốc, h&agrave;ng loạt c&ocirc;ng ty, dự &aacute;n bất động sản rơi v&agrave;o vũng lầy th&igrave; vẫn c&oacute; kh&ocirc;ng &iacute;t những chủ đầu tư biết vươn l&ecirc;n, chớp lấy cơ hội để khẳng định vị thế tr&ecirc;n thị trường. Đứng từ g&oacute;c nh&igrave;n của GS. TS Đặng H&ugrave;ng V&otilde;: &ldquo; chỉ c&oacute; những nh&agrave; đầu tư kh&ocirc;ng biết l&agrave;m mới rơi v&agrave;o ho&agrave;n cảnh kh&oacute; khăn&rdquo; th&igrave; c&oacute; thể thấy b&iacute; quyết th&agrave;nh c&ocirc;ng của những chủ đầu tư n&agrave;y kh&ocirc;ng kh&oacute; để l&yacute; giải, đ&oacute; ch&iacute;nh l&agrave;: Lu&ocirc;n sẵn s&agrave;ng chi tiền đầu tư v&agrave;o hạ tầng v&agrave; tiện &iacute;ch. Điển h&igrave;nh l&agrave; khu đ&ocirc; thị Xu&acirc;n Phương, khu đ&ocirc; thị được đầu tư kỹ lưỡng ngay từ kh&acirc;u l&ecirc;n phương &aacute;n quy hoạch tổng thể:</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/22/11/111811baoxaydung_14.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 333px;" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Trong cơn sốt bất động sản, c&aacute;c nh&agrave; đầu tư lu&ocirc;n tối đa h&oacute;a diện t&iacute;ch x&acirc;y dựng để kiếm lợi nhuận th&igrave; quy hoạch tổng thể của Viglacera lu&ocirc;n gi&agrave;nh mật độ lớn cho c&ocirc;ng vi&ecirc;n, hồ điều h&ograve;a, tạo n&ecirc;n kh&ocirc;ng gian sống h&agrave;i h&ograve;a cho cư d&acirc;n.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	B&ecirc;n cạnh đ&oacute;, c&aacute;c c&ocirc;ng tr&igrave;nh được đầu tư x&acirc;y dựng tốt với c&aacute;c vật liệu x&acirc;y dựng cao cấp như gạch b&ecirc; t&ocirc;ng kh&iacute; chưng &aacute;p c&aacute;ch &acirc;m, c&aacute;ch nhiệt, hộp k&iacute;nh low E c&aacute;ch nhiệt, kết hợp với thiết kế đơn giản gi&uacute;p mỗi căn nh&agrave; tận dụng được tối đa &aacute;nh s&aacute;ng v&agrave; kh&ocirc;ng kh&iacute; tự nhi&ecirc;n</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/22/11/111812baoxaydung_15.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 333px;" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Kh&ocirc;ng chỉ được ho&agrave;n thiện về x&acirc;y dựng, hệ thống hạ tầng kỹ thuật như điện nước, viễn th&ocirc;ng, internet cũng đ&atilde; được chuẩn bị sẵn s&agrave;ng để đ&oacute;n c&aacute;c cư d&acirc;n về ở.</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/22/11/111812baoxaydung_16.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 311px;" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Ri&ecirc;ng với dự &aacute;n biệt thự liền kề, khi m&agrave; đa số c&aacute;c cư d&acirc;n đều di chuyển bằng &ocirc; t&ocirc; th&igrave; hệ thống đường x&aacute; nội khu cũng l&agrave; một mối quan t&acirc;m lớn của người mua nh&agrave;.</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/22/11/111812baoxaydung_17.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 304px;" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Hệ thống đường rộng, tho&aacute;ng, chất lượng đảm bảo gi&uacute;p chủ đầu tư Viglacera ghi một điểm tốt trong mắt kh&aacute;ch h&agrave;ng.</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Đặc biệt d&ugrave; l&agrave; sản phẩm nhắm tới ph&acirc;n kh&uacute;c kh&aacute;ch h&agrave;ng n&agrave;o c&aacute;c chủ đầu tư th&agrave;nh c&ocirc;ng cũng lu&ocirc;n biết chi tiền cho c&aacute;c dịch vụ tiện &iacute;ch, thậm ch&iacute; sẵn s&agrave;ng đầu tư gia tăng tiện &iacute;ch nếu thấy cần thiết. B&ecirc;n cạnh s&acirc;n tennis, c&ocirc;ng vi&ecirc;n, nh&agrave; h&agrave;ng... khu đ&ocirc; thị Xu&acirc;n Phương sẽ được chủ đầu tư trang bị th&ecirc;m bể bơi, to&agrave;n bộ thiết kế, giải ph&aacute;p x&acirc;y dựng đ&atilde; được thực hiện v&agrave; sẵn s&agrave;ng triển khai trong thời gian tới.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/22/11/111812baoxaydung_18.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 354px;" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">Kế hoạch x&acirc;y dựng chi tiết bể bơi tại khu đ&ocirc; thị</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/hiep/102014/22/11/111813baoxaydung_19.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 333px;" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 205);">S&acirc;n tennis trong khu đ&ocirc; thị Xu&acirc;n Phương</span></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Kh&ocirc;ng chỉ được đầu tư kỹ lưỡng về cơ sở hạ tầng, c&ugrave;ng với vị tr&iacute; đẹp nằm ở trung t&acirc;m quận Nam Từ Li&ecirc;m, chủ đầu tư Viglacera c&ograve;n đưa ra một mức gi&aacute; hợp l&yacute;, chỉ từ 3,5 tỷ/căn chưa bao gồm thuế, kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; thể sở hữu một căn liền kề với diện t&iacute;ch hơn 80 m2.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Điểm qua những l&yacute; do tr&ecirc;n đ&acirc;y c&oacute; thể thấy việc khu đ&ocirc; thị Xu&acirc;n Phương của Viglacera đ&atilde; lấp đầy gần hết l&agrave; điều ho&agrave;n to&agrave;n dễ hiểu. Với mật độ d&acirc;n số cao như thủ đ&ocirc; H&agrave; Nội, nhu cầu sở hữu v&agrave; đầu tư v&agrave;o nh&agrave; ở l&agrave; lu&ocirc;n lu&ocirc;n hiện hữu, tuy nhi&ecirc;n để thuyết phục được kh&aacute;ch h&agrave;ng, c&aacute;c nh&agrave; ph&aacute;t triển dự &aacute;n bất động sản cần phải t&igrave;m ra c&aacute;ch thức v&agrave; con đường bền vững, l&acirc;u d&agrave;i. Đ&acirc;y l&agrave; điều m&agrave; những chủ đầu tư th&agrave;nh c&ocirc;ng trong thời gian qua đ&atilde; l&agrave;m được. V&agrave; đ&acirc;y cũng l&agrave; ti&ecirc;u ch&iacute; để c&aacute;c nh&agrave; ph&aacute;t triển dự &aacute;n bất động sản của Việt Nam hướng tới để kh&ocirc;ng chỉ vượt qua kh&oacute; khăn m&agrave; c&ograve;n x&acirc;y dựng n&ecirc;n một thị trường l&agrave;nh mạnh, bền vững.</p>', 0, '2014-10-23 10:32:48', '', 1, 'a:5:{s:6:"avatar";s:34:"1718810298_111811baoxaydung_14.jpg";s:6:"photos";a:0:{}s:6:"videos";a:0:{}s:5:"files";a:0:{}s:11:"user_upload";s:8:"derasoft";}', 4, 1),
(5, 1, 4, 'khong-co-phi-boi-tron-trong-cap-so-do', 'Không có phí bôi trơn trong cấp sổ đỏ', 'Không có phí bôi trơn trong cấp sổ đỏ', '(Xây dựng) - Đây là khẳng định của ông Vũ Ngọc Phụng - Phó Chủ tịch UBND quận Hà Đông - Hà Nội tại cuộc họp giao ban báo chí của Thành ủy Hà Nội, chiều ngày 21/10/2014.', '<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	<strong style="margin: 0px; padding: 0px;">(X&acirc;y dựng) - Đ&acirc;y l&agrave; khẳng định của &ocirc;ng Vũ Ngọc Phụng - Ph&oacute; Chủ tịch UBND quận H&agrave; Đ&ocirc;ng - H&agrave; Nội tại cuộc họp giao ban b&aacute;o ch&iacute; của Th&agrave;nh ủy H&agrave; Nội, chiều ng&agrave;y 21/10/2014. &ldquo;Kh&ocirc;ng c&oacute; chi ph&iacute; dịch vụ, b&ocirc;i trơn hay văn ph&ograve;ng phẩm trong việc cấp sổ đỏ tr&ecirc;n địa b&agrave;n quận&rdquo; - &ocirc;ng Phụng n&oacute;i.</strong></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: center;">\r\n	<img alt="" src="http://www.baoxaydung.com.vn/stores/news_dataimages/vananh/102014/21/23/233159baoxaydung_3.jpg" style="margin: 0px; padding: 0px; width: 500px; height: 375px;" /></p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Theo &ocirc;ng Phụng, từ ng&agrave;y 1/1/2014 đến ng&agrave;y 31/8/2014, quận H&agrave; Đ&ocirc;ng tiếp nhận v&agrave; cấp giấy chứng nhận quyền sử dụng đất cho 3.280 hồ sơ. Quận l&agrave;m theo c&aacute;ch chỉ kiểm tra hồ sơ c&aacute; nh&acirc;n, c&ograve;n việc quy hoạch diện t&iacute;ch ra sao l&agrave; do Sở TN&amp;MT thẩm định. Từ ng&agrave;y 1/9/2014, việc cấp giấy chứng nhận quyền sử dụng đất tr&ecirc;n địa b&agrave;n quận được b&agrave;n giao về Sở TN&amp;MT.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	&Ocirc;ng Phụng cho biết: D&ugrave; l&agrave; nh&agrave; đầu tư thứ ph&aacute;t, thậm ch&iacute; l&agrave; từng hộ mang hồ sơ đi xin cấp giấy chứng nhận quyền sử dụng đất, l&agrave; c&oacute; thể được cấp. Do đ&oacute;, việc quản l&yacute; cấp sổ đỏ tr&ecirc;n địa b&agrave;n quận đang gặp rất nhiều kh&oacute; khăn.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Trước những th&ocirc;ng tin về bất cập trong b&agrave;n giao sổ đỏ tại dự &aacute;n Văn Ph&uacute; Invest, &ocirc;ng Phụng cho biết: Quận sẵn s&agrave;ng v&agrave;o cuộc v&agrave; phối hợp với cơ quan b&aacute;o ch&iacute;.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	&ldquo;Quận H&agrave; Đ&ocirc;ng cấp giấy chứng nhận cho nh&agrave; chung cư kh&ocirc;ng bao giờ c&oacute; những chi ph&iacute; lớn, chỉ c&oacute; ph&iacute; giấy chứng nhận 25.000 đồng hay 100.000 đồng b&ecirc;n cạnh ph&iacute; trước bạ, kh&ocirc;ng c&oacute; khoản b&ocirc;i trơn hay văn ph&ograve;ng phẩm&rdquo; - &ocirc;ng Ph&uacute; khẳng định.</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Trong năm 2014, chỉ ti&ecirc;u của quận H&agrave; Đ&ocirc;ng l&agrave; phải cấp khoảng 8.000 giấy chứng nhận quyền sử dụng đất. Hiện quận đ&atilde; cấp được khoảng 4.300 giấy chứng nhận (52%), trong đ&oacute; c&oacute; khoảng 3.280 l&agrave; chứng nhận cho người d&acirc;n ở c&aacute;c khu đ&ocirc; thị. Số c&ograve;n lại l&agrave; của đất dịch vụ, đất đấu gi&aacute;, đất t&aacute;i định cư, đất thổ cư&hellip;</p>\r\n<p style="font-family: Arial, Helvetica, sans-serif; margin: 0px 0px 1.25em; padding: 0px; line-height: 1.3; color: rgb(0, 0, 0); text-align: justify;">\r\n	Được biết, quận đ&atilde; c&oacute; lịch l&agrave;m việc với c&aacute;c chủ đầu tư tr&ecirc;n địa b&agrave;n về việc cấp giấy chứng nhận quyền sử dụng đất cho người d&acirc;n.</p>', 0, '2014-10-23 10:34:09', '', 1, 'a:5:{s:6:"avatar";s:33:"2039155316_233159baoxaydung_3.jpg";s:6:"photos";a:0:{}s:6:"videos";a:0:{}s:5:"files";a:0:{}s:11:"user_upload";s:8:"derasoft";}', 5, 0);
