// Modify the layout of table in the dialog (since it use the same template as chechkout/cart table)
function modifyTableToDialogTable(tableId, productQty, unitPrice, totalPrice) {
    // Remove remove product button (rightmost in product list)
    $jq('#' + tableId).find('tbody tr.first td').last().remove();

    // Remove remove product text (in mobile)
    $jq('#' + tableId).find('tbody .btn-remove-font-p').remove();

    // Remove product edit link (Under product name)
    $jq('#' + tableId).find('tbody p.product-name-edit').remove();

    // Remove tooltip icon on mobile
    $jq('#' + tableId).find('tbody .mobile-cart-tr .product-shop-lead-time .i-icoNote').remove();

    // Change the product quantity to the quantity added (initially it is the overall qty in cart)
    var prodQtyElem = document.createElement('span');
    prodQtyElem.setAttribute("class", "product-shop-qty-added");
    prodQtyElem.innerHTML = productQty;
    $jq('#' + tableId).find('tbody .product-shop-list-info-quantity').html(prodQtyElem);

    $jq('#' + tableId).find('tbody tr.first td span.cart-price').first().find('.price').html(unitPrice);
    $jq('#' + tableId).find('tbody tr.mobile-cart-tr .cart-price').first().find('.price').html(unitPrice);
    // Change total price according the the number of product added, instead of total price in cart
    $jq('#' + tableId).find('tbody tr.first td').last().find('.price').html(totalPrice);
}

